
public class CustomerLister {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Array that initializes elements 
		String[] customerNames = {"Cathy", "Ben", "Jorge", "Wanda", "Freddie"};
		
		System.out.println("The customer names are: ");
		//Enhaced loop to print array elements 
		for(String x : customerNames) {
			System.out.println(x);
		}
	
	}

}
